    static public boolean isPossible(int[] nums) {

        Arrays.sort(nums);
        for (int n:nums) {
            System.out.print(n + ",");
        }

        System.out.println("---");
        boolean[] visited = new boolean[nums.length];

        int operatedTarget = nums[0];
        int ansCounts = 0;
        int sequenceCount = 1;
        HashMap<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
//            System.out.println("i:>>>>> " + i);
            sequenceCount = 1;
            operatedTarget = nums[i];
            if (visited[i]) {
//                System.out.println(i + ", VISITED!");
                continue;
            }
            if (map.size() > 0) {
//                System.out.println(valueMap);
                System.out.println("contain " + nums[i] + " ?:> " + map.containsKey(nums[i] - 1) + ", has residual ?:> " + (map.getOrDefault(nums[i] - 1, 0) > 0));
                if (map.containsKey(nums[i] - 1) && map.get(nums[i] - 1) > 0) {
                    visited[i] = true;
                    map.put((nums[i] - 1), map.get(nums[i] - 1) - 1);
                    map.put((nums[i]), map.getOrDefault(nums[i], 0) + 1);
                    ansCounts += 1;
                    System.out.println("Chained i:> " + i);
                    continue;
                }
            }
//            if (i == nums.length - 1) {
//
//            }
            Stack<Integer> path = new Stack<>();
            path.push(nums[i]);
            for (int j = i + 1; j < nums.length; j++) {
//                System.out.print("i:> " + i + ", j:> " + j);
                if (visited[j]) {
//                    System.out.println(", VISITED!");
                    continue;
                }
//                System.out.println("");
                if (nums[j] - operatedTarget == 1) {
//                    System.out.println("i:> " + i + ", nums[" + j + "]:> " + nums[j] + ", operatedTarget:> " + operatedTarget + ", sequenceCount:> " + sequenceCount);
                    operatedTarget = nums[j];
                    visited[j] = true;
                    sequenceCount++;
                    path.push(nums[j]);
                    if (sequenceCount == 3) {
//                        sequenceCount = 1;
//                        operatedTarget = nums[i];
                        map.put(nums[j], map.getOrDefault(nums[j], 0) + 1);
                        ansCounts += 3;
                        System.out.println("FIND:> " + path);
                        break;
                    }
                } else {
                    if (map.containsKey(nums[j] - 1) && map.get(nums[j] - 1) > 0) {
                        visited[j] = true;
                        map.put((nums[j] - 1), map.get(nums[j] - 1) - 1);
                        map.put((nums[j]), map.getOrDefault(nums[j], 0) + 1);
                        ansCounts += 1;
                    }
//                    System.out.println("contain " + nums[j] + " ?:> " + valueMap.containsKey(nums[j] - 1));
                }
            }
        }


        System.out.println("");
        System.out.println(map);
        System.out.println("nums.length:> " + nums.length + ", ansCounts:> " + ansCounts);
        return ansCounts == nums.length;
    }
